mapper.update:
UPDATE t_system SET 
    Status=#{Status},
    UpdatePerson=#{UpdatePerson},
    UpdateTime=#{UpdateTime}
WHERE 
    SystemCode=#{SystemCode}

UPDATE t_system SET 
    Status=99,
    UpdatePerson=#{UpdatePerson},
    UpdateTime=#{UpdateTime}
WHERE
    SystemCode=#{SystemCode}

mapper.select:
SELECT a.*, (SELECT count(b.ModuleId) FROM t_module b WHERE b.Status!=99) as childCoutn FROM t_system a WHERE a.Status!=99
AND a.ShortName LIKE CONCAT('%','${ShortName}','%' )
AND a.FullName LIKE CONCAT('%','${FullName}','%' )
and a.Status=#{Status}
and a.SystemCode IN
#{item}

mapper.insert:
INSERT INTO t_system_module_rela ( UUID, ModuleId, SystemId, Status, CreatePerson, CreateTime, UpdatePerson, UpdateTime
) VALUES
( #{lt.UUID}, #{lt.ModuleId}, #{lt.SystemId}, #{lt.Status}, #{lt.CreatePerson}, #{lt.CreateTime}, #{lt.UpdatePerson}, #{lt.UpdateTime} )
