mapper.update:
UPDATE SYSTEMMANAGER SET 
    STATUS=#{Status},
    UPDATEPERSON=#{UpdatePerson},
    UPDATETIME=#{UpdateTime}
WHERE 
    SYSTEMCODE=#{SystemCode}

UPDATE SYSTEMMANAGER SET 
    STATUS=99,
    UPDATEPERSON=#{UpdatePerson},
    UPDATETIME=#{UpdateTime}
WHERE
    SYSTEMCODE=#{SystemCode}
